css:
.einv-container { font-family: 'Poppins', Arial, sans-serif; padding: 20px; }
.einv-header { display: flex; justify-content: space-between; border-bottom: 2px solid #a404e4; padding-bottom: 15px; margin-bottom: 20px; }
.einv-title { font-size: 24px; font-weight: 700; color: #a404e4; }
.einv-qr { width: 120px; height: 120px; }
.einv-section { margin-bottom: 20px; }
.einv-section-title { font-size: 14px; font-weight: 600; color: #333; border-bottom: 1px solid #e0e0e0; padding-bottom: 5px; margin-bottom: 10px; }
.einv-grid { display: grid; grid-template-columns: 1fr 1fr; gap: 20px; }
.einv-field { margin-bottom: 8px; }
.einv-label { font-size: 11px; color: #666; text-transform: uppercase; }
.einv-value { font-size: 13px; color: #333; font-weight: 500; }
.einv-table { width: 100%; border-collapse: collapse; margin-top: 10px; }
.einv-table th { background: #f5f5f5; padding: 10px; text-align: left; font-size: 11px; text-transform: uppercase; color: #666; }
.einv-table td { padding: 10px; border-bottom: 1px solid #e0e0e0; font-size: 12px; }
.einv-totals { margin-top: 20px; text-align: right; }
.einv-total-row { display: flex; justify-content: flex-end; gap: 50px; padding: 5px 0; }
.einv-total-label { color: #666; }
.einv-total-value { font-weight: 600; min-width: 100px; }
.einv-grand-total { font-size: 18px; color: #a404e4; border-top: 2px solid #a404e4; padding-top: 10px; }
.einv-irn-box { background: #f9f5fc; border: 1px solid #e0d0f0; border-radius: 8px; padding: 15px; margin-top: 20px; }
.einv-irn-label { font-size: 11px; color: #666; }
.einv-irn-value { font-size: 14px; font-weight: 600; color: #a404e4; word-break: break-all; }
.einv-footer { margin-top: 30px; padding-top: 15px; border-top: 1px solid #e0e0e0; font-size: 10px; color: #999; text-align: center; }
.einv-bilingual { display: flex; justify-content: space-between; }
.einv-ar { direction: rtl; text-align: right; }
